#include <iostream>
using namespace std;

int factorial(int n) {
    if (n <= 1) return 1;
    return n * factorial(n - 1);
}

int main() {
    int numero = 5;
    cout << "Factorial de " << numero << " = " << factorial(numero) << endl;
    return 0;
}
