public class Factorial {
    public static int calcular(int n) {
        if (n <= 1) return 1;
        return n * calcular(n - 1);
    }

    public static void main(String[] args) {
        int numero = 5;
        System.out.println("Factorial de " + numero + " = " + calcular(numero));
    }
}

import java.util.Scanner;

public class Palindromo {
    public static boolean esPalindromo(String texto) {
        String limpio = texto.replaceAll("[^a-zA-Z]", "").toLowerCase();
        return new StringBuilder(limpio).reverse().toString().equals(limpio);
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        System.out.print("Introduce una palabra o frase: ");
        String entrada = sc.nextLine();
        System.out.println(esPalindromo(entrada) ? "Es un palíndromo" : "No es un palíndromo");
    }
}
